export const imageLoader = ({ src, width, quality }: {
  src: string
  width: number
  quality?: number
}) => {
  return `${src}?w=${width}&q=${quality || 75}`
}

export const getOptimizedImageProps = (
  src: string,
  alt: string,
  width: number,
  height: number
) => ({
  src,
  alt,
  width,
  height,
  loader: imageLoader,
  quality: 85,
  placeholder: 'blur' as const,
  blurDataURL: 'data:image/jpeg;base64,'
})